package chapter08;import java.util.concurrent.CountDownLatch;import java.util.concurrent.TimeUnit;/** *  * @author tengfei.fangtf * @version $Id: CountDownLatchTest.java, v 0.1 2015-8-1 ÉÏÎç12:08:52 tengfei.fangtf Exp $ */public class CountDownLatchTest {    static CountDownLatch c = new CountDownLatch(2);    public static void main(String[] args) throws InterruptedException {        new Thread(new Runnable() {            @Override            public void run() {                System.out.println(1);                c.countDown();                System.out.println(2);                c.countDown();                try {                    TimeUnit.SECONDS.sleep(5);                    System.out.println(4);                } catch (InterruptedException e) {                    e.printStackTrace();                }            }        }).start();        c.await();        System.out.println("3");    }}